import { useState } from 'react'
import reactLogo from './assets/react.svg'
import viteLogo from './vite.svg'
import './App.css'

function App() {
  const [inputValue, setInputValue] = useState('');
  const [submittedValues, setSubmittedValue] = useState([]);
  const [hoveredTicker, setHoveredTicker] = useState(null);
  const [selectedTicker, setSelectedTicker] = useState(null);
  const [tickerData, setTickerData] = useState(null);

  const handleInputChange = (e) => {
      setInputValue(e.target.value);
  };

  const handleFetchData = async (ticker) => {
    const [data, setData] = useState([{}])

    useEffect(() => {
        fetch("/get_stock_data").then(
            res => res.json()
        ).then(
            tickerData => {
                setTickerData(tickerData)
                console.log(tickerData)
            }
        )
    }, [])

    // try {
    //     const response = await fetch('/get_stock_data', {
    //         method: 'POST',
    //         headers: {
    //             'Content-Type': 'application/json',
    //         },
    //         body: JSON.stringify({ ticker }),
    //     });

    //     if (!response.ok) {
    //         throw new Error('Failed to fetch data from server');
    //     }
    //     console.log(response);
    //     const jsonData = await response.json();
    //     console.log(jsonData);
    //     setTickerData(jsonData);
    //     console.log(tickerData);
    // } catch (error) {
    //     console.error('Error fetching data from server:', error);
    // }
  };

  const handleSubmit = (e) => {
      e.preventDefault();
      setSubmittedValue((prevValues) => [...prevValues, inputValue]);
      setInputValue('');
  };

  const handleHover = (ticker) => {
    console.log(`Hovered on ticker: ${ticker}`);
    setHoveredTicker(ticker);
    handleFetchData(ticker);
  }

  const handleTickerClick = async (ticker) => {
      setSelectedTicker(ticker);
  }

  return (
    <>
      <h1 className="header">StockHub</h1>
      <form onSubmit={handleSubmit} className="input">
          <input
            type="text"
            value={inputValue}
            onChange={handleInputChange}
            placeholder="Enter ticker name"
            className="value"
            />
            <button type="submit" className="button">Add</button>
      </form>
      <div className="ticker-grid">
        {submittedValues.map((ticker, index) => (
            <div
                key={index}
                className={`ticker-box ${hoveredTicker === ticker ? 'hovered' : ''}`}
                onMouseOver={() => handleHover(ticker)}
                onMouseOut={() => setHoveredTicker(null)}
                >
                <div>{ticker}</div>
                <div className="additional-content">
                    <p>Additional Data:</p>
                    {tickerData && (
                        <>
                            <p>Current Price: {tickerData.currentPrice}</p>
                            <p>Open Price: {tickerData.openPrice}</p>
                        </>
                    )}
                </div>
            </div>
        ))}
      </div>
    </>
  );
}

export default App
