      let API_Call_1 = `https://api.polygon.io/v3/reference/tickers?ticker=${ticker}&active=true&apiKey=${API_KEY}`;
      let API_Call_2 = `https://api.polygon.io/v1/open-close/${ticker}/2023-01-09?adjusted=true&apiKey=${API_KEY}`;
    //   rest.stocks
    //     .aggregates("AAPL", 1, "day", "2019-01-01", "2019-02-01")
    //     .then((data) => {
    //         console.log(data)
    //     })
    //     .catch((e) => {
    //         console.error("An error happened:", e)
    //     })
      fetch(API_Call_1)
        .then(
            function(response) {
                return response.json();
            }
        ).then(
            function(data) {
                console.log(data);
                const name = data["name"];
                setTickerData({ ticker, name });
            }
        )

      fetch(API_Call_2)
        .then(
            function(response) {
                return response.json();
            }
        ).then(
            function(data) {
                console.log(data);
                const mostRecentOpenPrice = data['open'];
                const mostRecentClosePrice = data['close'];
                // const mostRecentDate = Object.keys(data['Time Series (Daily)'])[0];
                // const mostRecentOpenPrice = data['Time Series (Daily)'][mostRecentDate]['1. open'];
                // const mostRecentClosePrice = data['Time Series (Daily)'][mostRecentDate]['4. close'];

                setTickerData((prevData) => ({
                    ...prevData,
                    [ticker]: { mostRecentOpenPrice, mostRecentClosePrice },
                }));
                console.log(setTickerData);
            }
        )
  }

  useEffect(() => {
